javascript.solution:
/**
 * Funzione per calcolare la somma di due numeri
 * @param {number} a - Primo numero
 * @param {number} b - Secondo numero
 * @returns {number} La somma dei due numeri
 */
function calcola(a, b) {
    // Calcola la somma dei due parametri
    var risultato = a + b;
    // Restituisce il risultato
    return risultato;
}

// Definizione delle variabili
var num1 = 10;  // Primo numero
var num2 = 5;   // Secondo numero
// Chiama la funzione calcola con i numeri definiti
var risp = calcola(num1, num2);
// Stampa il risultato
console.log(risp);

exercises.testCode:
function testSolution(code, language) {
  if (language === 'python') {
    // Verifica se ci sono almeno 3 commenti nel codice Python
    const commentCount = (code.match(/#/g) || []).length;
    return commentCount >= 3;
  } else if (language === 'javascript') {
    // Verifica se ci sono almeno 3 commenti in JavaScript
    const singleLineComments = (code.match(/\/\//g) || []).length;
    const multiLineCommentsStart = (code.match(/\/\*/g) || []).length;
    const multiLineCommentsEnd = (code.match(/\*\//g) || []).length;
    return (singleLineComments + multiLineCommentsStart) >= 3;
  }
  return false;
}

