function testSolution(code, language) {
  if (language === 'python') {
    // Verifica se il codice usa input() e print()
    return code.includes('input(') && code.includes('print(');
  } else if (language === 'javascript') {
    // Verifica se il codice usa prompt() e console.log()
    return code.includes('prompt(') && code.includes('console.log(');
  }
  return false;
}
